const mapReducer = (state, action) => {

    switch(action.type) {

        case 'add_marker' : 
            return { ...state, markers: [...(state.markers || []), action.marker]}

        case 'delete_marker' :
            const markers = [...state.markers]
            markers.splice(action.index, 1)
            return { ...state, markers: [...( markers || [])] }

        case 'set_user_infos' :
            const user = {...state.user} || {}

            user.firstName = action.user.firstName
            user.lastName = action.user.lastName
            user.mail = action.user.mail

            return {...state, user }

        default : 
            return {...state}
    }
}

export default mapReducer
